mapper.select:
SELECT
	account_no AS accountNo,
	created_by AS createdBy,
	created_at AS createdAt,
	updated_by AS updatedBy,
	updated_at AS updatedAt,
	is_deleted AS isDeleted,
	account_id AS accountId,
	account_name AS accountName,
	password,
	birthdate,
	sex_kbn AS sexKbn,
	birthplace_prefecture_kbn_code AS birthplacePrefectureKbnCode,
	resident_prefecture_kbn_code AS residentPrefectureKbnCode,
	free_memo AS freeMemo,
	authority_kbn_code AS authorityKbnCode,
	last_login_datetime AS lastLoginDatetime,
	login_failure_count AS loginFailureCount
FROM
	common.account

	 AND account_no = #{accountNo}
	 AND is_deleted = #{isDeleted}
	 AND account_id = #{accountId}
	 AND account_name = #{accountName}
	 AND password = #{password}
	 AND birthdate = #{birthdate}
	 AND sex_kbn = #{sexKbn}::common.sex_enum
	 AND birthplace_prefecture_kbn_code = #{birthplacePrefectureKbnCode}
	 AND resident_prefecture_kbn_code = #{residentPrefectureKbnCode}
	 AND free_memo = #{freeMemo}
	 AND authority_kbn_code = #{authorityKbnCode}
	 AND last_login_datetime = #{lastLoginDatetime}
	 AND login_failure_count = #{loginFailureCount}

SELECT
	COUNT(*)
FROM
	common.account

	 AND account_no = #{accountNo}
	 AND is_deleted = #{isDeleted}
	 AND account_id = #{accountId}
	 AND account_name = #{accountName}
	 AND password = #{password}
	 AND birthdate = #{birthdate}
	 AND sex_kbn = #{sexKbn}::common.sex_enum
	 AND birthplace_prefecture_kbn_code = #{birthplacePrefectureKbnCode}
	 AND resident_prefecture_kbn_code = #{residentPrefectureKbnCode}
	 AND free_memo = #{freeMemo}
	 AND authority_kbn_code = #{authorityKbnCode}
	 AND last_login_datetime = #{lastLoginDatetime}
	 AND login_failure_count = #{loginFailureCount}

mapper.update:
UPDATE 
	common.account

	updated_at = NOW()
	 , updated_by = #{target.updatedBy}
	 , is_deleted = #{target.isDeleted}
	 , account_id = #{target.accountId}
	 , account_name = #{target.accountName}
	 , password = #{target.password}
	 , birthdate = #{target.birthdate}
	 , sex_kbn = #{target.sexKbn}::common.sex_enum
	 , birthplace_prefecture_kbn_code = #{target.birthplacePrefectureKbnCode}
	 , resident_prefecture_kbn_code = #{target.residentPrefectureKbnCode}
	 , free_memo = #{target.freeMemo}
	 , authority_kbn_code = #{target.authorityKbnCode}
	 , last_login_datetime = #{target.lastLoginDatetime}
	 , login_failure_count = #{target.loginFailureCount}


