mapper.select:
SELECT
	kbn_class_code AS kbnClassCode,
	kbn_code AS kbnCode,
	created_by AS createdBy,
	created_at AS createdAt,
	sort_order AS sortOrder,
	kbn_group_code AS kbnGroupCode,
	kbn_class_japanese_name AS kbnClassJapaneseName,
	kbn_group_japanese_name AS kbnGroupJapaneseName,
	kbn_japanese_name AS kbnJapaneseName,
	kbn_class_english_name AS kbnClassEnglishName,
	kbn_group_english_name AS kbnGroupEnglishName,
	kbn_english_name AS kbnEnglishName,
	explanation
FROM
	common.kbn_mst

	 AND kbn_class_code = #{kbnClassCode}
	 AND kbn_code = #{kbnCode}
	 AND sort_order = #{sortOrder}
	AND kbn_group_code = #{kbnGroupCode}
	 AND kbn_class_japanese_name = #{kbnClassJapaneseName}
	AND kbn_group_japanese_name = #{kbnGroupJapaneseName}
	 AND kbn_japanese_name = #{kbnJapaneseName}
	 AND kbn_class_english_name = #{kbnClassEnglishName}
	AND kbn_group_english_name = #{kbnGroupEnglishName}
	 AND kbn_english_name = #{kbnEnglishName}
	 AND explanation = #{explanation}

SELECT
	COUNT(*)
FROM
	common.kbn_mst

	 AND kbn_class_code = #{kbnClassCode}
	 AND kbn_code = #{kbnCode}
	 AND sort_order = #{sortOrder}
	AND kbn_group_code = #{kbnGroupCode}
	 AND kbn_class_japanese_name = #{kbnClassJapaneseName}
	AND kbn_group_japanese_name = #{kbnGroupJapaneseName}
	 AND kbn_japanese_name = #{kbnJapaneseName}
	 AND kbn_class_english_name = #{kbnClassEnglishName}
	AND kbn_group_english_name = #{kbnGroupEnglishName}
	 AND kbn_english_name = #{kbnEnglishName}
	 AND explanation = #{explanation}

mapper.update:
UPDATE 
	common.kbn_mst

