mapper.select:
SELECT
	COUNT(*)
FROM
	photo.photo_favorite

	 AND account_no = #{accountNo}
	 AND favorite_photo_account_no = #{favoritePhotoAccountNo}
	 AND favorite_photo_no = #{favoritePhotoNo}

mapper.delete:
DELETE FROM
	photo.photo_favorite

	 AND account_no = #{accountNo}
	 AND favorite_photo_account_no = #{favoritePhotoAccountNo}
	 AND favorite_photo_no = #{favoritePhotoNo}
