mapper.select:
SELECT
	COUNT(*)
FROM
	photo.photo_mst

	 AND account_no = #{accountNo}
	 AND photo_no = #{photoNo}
	 AND is_deleted = #{isDeleted}
	 AND photo_at = #{photoAt}
	 AND location_no = #{locationNo}
	 AND image_file_path = #{imageFilePath}
	 AND photo_japanese_title = #{photoJapaneseTitle}
	 AND photo_english_title = #{photoEnglishTitle}
	 AND caption = #{caption}
	 AND direction_kbn = #{directionKbn}::photo.direction_enum
	 AND focal_length = #{focalLength}
	 AND f_value = #{fValue}
	 AND shutter_speed = #{shutterSpeed}
	 AND iso = #{iso}

mapper.update:
UPDATE 
	photo.photo_mst

	updated_at = NOW()
	 , updated_by = #{target.updatedBy}
	 , is_deleted = #{target.isDeleted}
	 , photo_at = #{target.photoAt}
	 , location_no = #{target.locationNo}
	 , image_file_path = #{target.imageFilePath}
	 , photo_japanese_title = #{target.photoJapaneseTitle}
	 , photo_english_title = #{target.photoEnglishTitle}
	 , caption = #{target.caption}
	 , direction_kbn = #{target.directionKbn}::photo.direction_enum
	 , focal_length = #{target.focalLength}
	 , f_value = #{target.fValue}
	 , shutter_speed = #{target.shutterSpeed}
	 , iso = #{target.iso}


	 AND account_no = #{condition.accountNo}
	 AND photo_no = #{condition.photoNo}
	 AND is_deleted = #{condition.isDeleted}
	 AND photo_at = #{condition.photoAt}
	 AND location_no = #{condition.locationNo}
	 AND image_file_path = #{condition.imageFilePath}
	 AND photo_japanese_title = #{condition.photoJapaneseTitle}
	 AND photo_english_title = #{condition.photoEnglishTitle}
	 AND caption = #{condition.caption}
	 AND direction_kbn = #{condition.directionKbn}::photo.direction_enum
	 AND focal_length = #{condition.focalLength}
	 AND f_value = #{condition.fValue}
	 AND shutter_speed = #{condition.shutterSpeed}
	 AND iso = #{condition.iso}
