mapper.select:
SELECT
	account_no AS accountNo,
	photo_no AS photoNo,
	tag_no AS tagNo,
	created_by AS createdBy,
	created_at AS createdAt,
	tag_japanese_name AS tagJapaneseName,
	tag_english_name AS tagEnglishName
FROM
	photo.photo_tag_mst

	 AND account_no = #{accountNo}
	 AND photo_no = #{photoNo}
	 AND tag_no = #{tagNo}
	 AND tag_japanese_name = #{tagJapaneseName}
	 AND tag_english_name = #{tagEnglishName}

SELECT
	COUNT(*)
FROM
	photo.photo_tag_mst

	 AND account_no = #{accountNo}
	 AND photo_no = #{photoNo}
	 AND tag_no = #{tagNo}
	 AND tag_japanese_name = #{tagJapaneseName}
	 AND tag_english_name = #{tagEnglishName}

mapper.update:
UPDATE 
	photo.photo_tag_mst

	 , tag_japanese_name = #{target.tagJapaneseName}
	 , tag_english_name = #{target.tagEnglishName}


	 AND account_no = #{condition.accountNo}
	 AND photo_no = #{condition.photoNo}
	 AND tag_no = #{condition.tagNo}
	 AND tag_japanese_name = #{condition.tagJapaneseName}
	 AND tag_english_name = #{condition.tagEnglishName}

mapper.delete:
DELETE FROM
	photo.photo_tag_mst

	 AND account_no = #{accountNo}
	 AND photo_no = #{photoNo}
	 AND tag_no = #{tagNo}
	 AND tag_japanese_name = #{tagJapaneseName}
	 AND tag_english_name = #{tagEnglishName}
