mapper.select:
SELECT
	account_no AS accountNo,
	photo_no AS photoNo,
	created_at AS createdAt,
	sort_order AS sortOrder
FROM
	photo.portfolio

	 AND account_no = #{accountNo}
	 AND photo_no = #{photoNo}
	 AND sort_order = #{sortOrder}

SELECT
	COUNT(*)
FROM
	photo.portfolio

	 AND account_no = #{accountNo}
	 AND photo_no = #{photoNo}
	 AND sort_order = #{sortOrder}

mapper.update:
UPDATE 
	photo.portfolio

	 , sort_order = #{target.sortOrder}


	 AND account_no = #{condition.accountNo}
	 AND photo_no = #{condition.photoNo}
	 AND sort_order = #{condition.sortOrder}

mapper.delete:
DELETE FROM
	photo.portfolio

	 AND account_no = #{accountNo}
	 AND photo_no = #{photoNo}
	 AND sort_order = #{sortOrder}
